"use strict";let modalOpen=document.querySelector(".map__button"),modalClose=document.querySelector(".popap__button-close"),popap=document.querySelector(".popap"),modalForm=document.querySelector(".popap__form"),modalLogin=document.querySelector("[name = user-name]"),modalEmail=document.querySelector("[name = user-email]"),modalText=document.querySelector("[name = user-text]");const KEY_ESC=27;modalOpen.addEventListener("click",function(a){a.preventDefault(),popap.classList.add("popap__open"),modalLogin.focus()}),modalClose.addEventListener("click",function(a){a.preventDefault(),popap.classList.remove("popap__open")}),window.addEventListener("keydown",function(a){a.keyCode!==KEY_ESC||popap.classList.contains("visually-hidden")||(a.preventDefault(),popap.classList.remove("popap__open"))}),modalForm.addEventListener("submit",function(a){modalLogin.value||modalEmail.value||(a.preventDefault(),popap.classList.add("popap__error"))});
